mapper.select:
SELECT user_name , user_nick, user_id, user_regdt
FROM users
WHERE user_id = #{user_id} ;

SELECT user_id , user_passwd , user_nick , user_name , user_phone , user_add , user_passQ , user_passA , user_enterSalay , user_enterCnt , user_enterHiredat
FROM users
WHERE user_id = #{user_id} ;

mapper.update:
UPDATE users SET user_passwd = #{user_passwd}, user_nick = #{user_nick}, user_name = #{user_name}, user_phone = #{user_phone}, user_add = #{user_add}, user_passQ = #{user_passQ}, user_passA = #{user_passA}, user_salay = #{user_salay}, user_cnt = #{user_cnt}, user_hiredate = #{user_hiredate},
WHERE
user_id = #{user_id};
