mapper.select:
SELECT X.*, Y.* FROM ( SELECT T.user_name, T.user_nick, T.user_id, TO_CHAR(T.reg_dt,'YYYY/MM/DD')) AS reg_dt, T.rnum AS no FROM ( SELECT C.*, ROW_NUMBER() OVER(ORDER BY reg_dt DESC) AS rnum FROM users C
) T WHERE rnum BETWEEN (#{pageSize} * (#{pageNum}-1)+1) AND (#{pageSize} * (#{pageNum}-1)+ #{pageSize}) ) X NATURAL JOIN ( SELECT COUNT(*) AS total_cnt FROM users
)Y

SELECT user_id , user_passwd , user_nick , user_name , user_phone , user_add , user_enterSalay , user_enterCnt , user_enterHiredat user_passQ , user_passA ,
FROM users
WHERE user_id = #{user_id} ;

mapper.update:
UPDATE users SET user_passwd = #{user_passwd}, user_nick = #{user_nick}, user_name = #{user_name}, user_phone = #{user_phone}, user_add = #{user_add}, user_passQ = #{user_passQ}, user_passA = #{user_passA}, user_salay = #{user_salay}, user_cnt = #{user_cnt}, user_hiredate = #{user_hiredate},
WHERE
user_id = #{user_id};
