mapper.select:
SELECT user_id , user_passwd , user_nick , user_name , user_phone , user_add , user_passQ , user_passA
FROM users
WHERE user_id = #{userId}

mapper.update:
UPDATE users SET user_passwd = #{userPasswd}, user_nick = #{userNick}, user_name = #{userName}, user_phone = #{userPhone}, user_add = #{userAdd}, user_passQ = #{userPassQu}, user_passA = #{userPassAn}
WHERE
user_id = #{userId}

