mapper.select:
SELECT X.*, Y.* FROM ( SELECT T.hire_no, T.hire_title, T.hire_body, T.user_id, TO_CHAR(T.hire_date,'YYYY/MM/DD') AS hire_date, TO_CHAR(T.hire_deadline,'YYYY/MM/DD') AS hire_deadline, T.hire_add, T.hire_edu, T.reg_id, TO_CHAR(T.reg_dt,'YYYY/MM/DD') AS reg_dt, T.rnum AS no FROM ( SELECT C.*, ROW_NUMBER() OVER(ORDER BY reg_dt DESC) AS rnum FROM hireinfo C
) T WHERE rnum BETWEEN (#{pageSize} * (#{pageNum}-1)+1) AND (#{pageSize} * (#{pageNum}-1)+ #{pageSize}) ) X NATURAL JOIN ( SELECT COUNT(*) AS total_cnt FROM hireinfo C
) Y
