mapper.select:
SELECT X.*, Y.* FROM ( SELECT T.hire_no, T.hire_title, T.hire_body, T.user_id, T.hire_date, T.hire_deadline, T.cl_check, T.reg_id, DECODE(TO_CHAR(T.reg_dt,'MM/DD'),TO_CHAR(sysdate,'MM/DD') ,TO_CHAR(T.reg_dt,'HH24:mm:SS'),TO_CHAR(T.reg_dt,'YYYY/MM/DD')) AS reg_dt, T.rnum AS no FROM ( SELECT C.*, ROW_NUMBER() OVER(ORDER BY reg_dt DESC) AS rnum FROM usercl C
) T WHERE rnum BETWEEN (#{pageSize} * (#{pageNum}-1)+1) AND (#{pageSize} * (#{pageNum}-1)+ #{pageSize}) ) X NATURAL JOIN ( SELECT COUNT(*) AS total_cnt FROM usercl C
) Y
