mapper.select:
SELECT 
	UserId
FROM Users

	WHERE	user_name=#{userName}
	AND		user_phone=#{userPhone}

SELECT 
	user_passwd

FROM Users

	WHERE	user_id=#{userId}
	AND		user_passq=#{userPassQ}
	AND		user_passa=#{userPassA}

mapper.insert:
INSERT INTO Users (
User_Id , User_Passwd , User_Nick , User_Name , User_Phone , User_Add , Enter_Salay , Enter_Cnt , Enter_Hiredate, User_PassQ , User_PassA , User_Phone , RegId , RegDt , ModId , ModDt ) VALUES ( #{userId}, #{userPasswd}, #{userNick}, #{userName} , #{userPhone}, #{userAdd} #{enterSalay}, #{enterCnt}, #{enterHiredate}, #{userPassQ}, #{userPassA}, #{userPhone}, #{regId}, SYSDAYE #{modId}, SYSDAYE
)
