mapper.select:
SELECT X.*, Y.* FROM ( SELECT T.cl_no, T.cl_title, T.cl_sungjang, T.cl_sang, T.cl_jangdan, T.cl_jiwon, T.cl_check, T.reg_id, T.reg_dt, T.rnum AS no FROM ( SELECT C.*, ROW_NUMBER() OVER(ORDER BY reg_dt DESC) AS rnum FROM usercl C
) T WHERE rnum BETWEEN (#{page_size} * (#{page_num}-1)+1) AND (#{page_size} * (#{page_num}-1)+ #{page_size}) ) X NATURAL JOIN ( SELECT COUNT(*) AS total_cnt FROM usercl
) Y
