mapper.select:
SELECT X.*, Y.* FROM ( SELECT T.reg_id, T.cv_date, T.cv_grade, T.cv_check, T.reg_dt, T.mod_id, T.mod_dt, T.rnum AS no FROM ( SELECT C.*, ROW_NUMBER() OVER(ORDER BY reg_dt DESC) AS rnum FROM usercv C
) T WHERE rnum BETWEEN (#{pageSize} * (#{pageNum}-1)+1) AND (#{pageSize} * (#{pageNum}-1)+ #{pageSize}) ) X NATURAL JOIN ( SELECT COUNT(*) AS total_cnt FROM usercv
) Y

SELECT reg_id, cv_date, cv_grade, cv_check, reg_dt, mod_id, mod_dt
FROM usercv
WHERE reg_id = #{reg_id} ;

mapper.insert:
INSERT INTO userlicense (
    lic_no,
    lic_name,
    lic_date,
    lic_score,
    reg_id
) VALUES (
    #{lic_no},
    #{lic_name},
    #{lic_date},
    #{lic_score},
    #{reg_id}
);

mapper.select:
SELECT X.*, Y.* FROM ( SELECT T.LIC_NO, T.LIC_NAME, T.LIC_DATE, T.LIC_SCORE, T.REG_ID, T.rnum AS no FROM ( SELECT C.*, ROW_NUMBER() OVER(ORDER BY LIC_DATE DESC) AS rnum FROM USERLICENSE C ) T WHERE rnum BETWEEN (#{pageSize} * (#{pageNum}-1)+1) AND (#{pageSize} * (#{pageNum}-1)+ #{pageSize})
) X NATURAL JOIN ( SELECT COUNT(*) AS total_cnt FROM USERLICENSE
) Y;

SELECT *
FROM userlicense
WHERE reg_id = #{reg_id}
  AND lic_no = #{lic_no};

SELECT X.*, Y.* FROM ( SELECT T.APPLY_NO, T.USER_ID, T.APPLY_DATE, T.HIRE_NO, T.rnum AS no FROM ( SELECT C.*, ROW_NUMBER() OVER(ORDER BY APPLY_DATE) AS rnum FROM HIREAPPLY C ) T WHERE rnum BETWEEN (#{pageSize} * (#{pageNum}-1)+1) AND (#{pageSize} * (#{pageNum}-1)+ #{pageSize})
) X NATURAL JOIN ( SELECT COUNT(*) AS total_cnt FROM HIREAPPLY
) Y;
